var login={init:function(){this.registerEvents()},registerEvents:function(){$("#frm-login").validate({errorClass:"red",ignore:[],rules:{username:"required",password:"required"}});$("#btn-login").on("click",function(n){if(n.preventDefault(),$("#frm-login").valid()){var t=$("#txt-username").val(),i=$("#txt-password").val();login.login(t,i)}});$("#txt-username").on("keypress",function(n){if($("#frm-login").valid()){var t=$("#txt-username").val(),i=$("#txt-password").val();n.which===13&&login.login(t,i)}});$("#txt-password").on("keypress",function(n){if($("#frm-login").valid()){var t=$("#txt-username").val(),i=$("#txt-password").val();n.which===13&&login.login(t,i)}})},login:function(n,t){var i={Username:n,Password:t,Rememberme:!1};$.ajax({type:"POST",data:{Model:i},dataType:"json",url:"/Admin/Login/Signin",success:function(n){n.Status?window.location.href="/Admin/Home/Index":common.notify(n.Message,"error")},error:function(n){console.log(n);common.notify("Error: "+n,"error")}})}};login.init();